import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserForm = ({ match }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    bio: ''
  });

  useEffect(() => {
    // Fetch user data if editing existing user
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:5555/users/${match.params.user_id}`);
        setFormData(response.data);
      } catch (error) {
        console.error('Fetch User Error:', error); // Handle error response
      }
    };

    if (match.params.userId) {
      fetchUserData();
    }
  }, [match.params.userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (match.params.userId) {
        await axios.put(`http://localhost:5555/users/${match.params.user_id}`, formData);
      } else {
        await axios.post('http://localhost:5555/users', formData);
      }
      console.log('User saved successfully');
    } catch (error) {
      console.error('Save User Error:', error); // Handle error response
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2>Create User</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="bio">Bio:</label>
          <textarea
            id="bio"
            name="bio"
            value={formData.bio}
            onChange={handleChange}
            rows={4}
          />
        </div>
        <button type="submit">Create User</button>
      </form>
    </div>
  );
};

export default UserForm
