import React, { useEffect } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

function TaskForm({ onTaskAdded, onTaskUpdated, editingTask, setEditingTask }) {
    const initialValues = {
        name: editingTask ? editingTask.name : '',
        description: editingTask ? editingTask.description : '',
        due_date: editingTask ? editingTask.due_date : ''
    };

    const validationSchema = Yup.object({
        name: Yup.string().required('Required'),
        description: Yup.string().required('Required'),
        due_date: Yup.string().required('Required')
    });

    useEffect(() => {
        // Reset form values when editingTask changes
        setValues(initialValues);
    }, [editingTask, setValues]);

    const handleSubmit = (values, { setSubmitting, resetForm }) => {
        if (editingTask) {
            onTaskUpdated({ ...values, id: editingTask.id });
        } else {
            onTaskAdded(values);
        }
        resetForm();
        setSubmitting(false);
    };

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={handleSubmit}
            enableReinitialize
        >
            {({ isSubmitting, setValues }) => (
                <Form className="bg-white shadow-sm rounded px-4 pt-4 pb-4 mb-4">
                    <div className="mb-3">
                        <label htmlFor="name" className="form-label text-gray-700">
                            Name
                        </label>
                        <Field
                            type="text"
                            name="name"
                            className="form-control"
                        />
                        <ErrorMessage name="name" component="div" className="text-danger small" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="description" className="form-label text-gray-700">
                            Description
                        </label>
                        <Field
                            as="textarea"
                            name="description"
                            className="form-control"
                            rows="3"
                        />
                        <ErrorMessage name="description" component="div" className="text-danger small" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="due_date" className="form-label text-gray-700">
                            Due Date
                        </label>
                        <Field
                            type="date"
                            name="due_date"
                            className="form-control"
                        />
                        <ErrorMessage name="due_date" component="div" className="text-danger small" />
                    </div>
                    <div className="d-flex justify-content-end">
                        <button
                            type="submit"
                            disabled={isSubmitting}
                            className="btn btn-primary"
                        >
                            {isSubmitting ? 'Submitting...' : editingTask ? 'Update Task' : 'Add Task'}
                        </button>
                    </div>
                </Form>
            )}
        </Formik>
    );
}

export default TaskForm;
